import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import Modal from '../components/Modal';

export default function Albums(props) {
  const [open, handleOpen] = useState(false)
  const { albums, handleDelete, currentUser } = props;
  return (
    <div>
      <h3>Albums</h3>
      {
        albums.map(food => (
          <React.Fragment key={food.id}>
            <Link to={`/foods/${food.id}`}><p>{food.name}</p></Link>
            {
              currentUser?.id === food.user_id &&
              <>
                <Link to={`/foods/${food.id}/edit`}><button>Edit</button></Link>
                <button onClick={() => handleOpen(food.id)}>delete</button>
              </>
            }
          </React.Fragment>
        ))
      }
      <br />
      <Link to='/foods/new'><button>Create</button></Link>
      {open && (
        <Modal
          open={open}
          handleOpen={handleOpen}
          handleDelete={handleDelete}
        />
      )}
    </div>
  )
}
